package javaPackage;

public class ConditionalStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		
		if (a%2==0)
		{
			System.out.println("The number is EVEN "+a);
		}
			
			else
			{
				System.out.println("The Number is ODD "+a);
		}

	}

}
